INSERT INTO `phpbb_privmsgs_text` VALUES (3284,'4f6d3d43e9','Scott, Heres another tasty recipe. I\'ve brewed this one seven times. It works fine. Also just change hop and you\'ll change the brew. English Pale ale \r\n\r\n\r\nBrewer: Brian Mosley Email: - \r\nBeer: English Pale ale Style: English Pale Ale \r\nType: All grain Size: 5.5 gallons \r\nColor: 6 HCU (~5 SRM)   \r\n Bitterness: 35 IBU \r\nOG: 1.060 FG: 1.008 \r\nAlcohol: 6.8% v/v (5.3% w/w) \r\nGrain: 12 lb. British pale\r\n1 lb. Dextrine malt (Cara-Pils) \r\nMash: 68% efficiency \r\nBoil: minutes SG 1.048 7 gallons \r\nHops: 1 oz. Kent Goldings (5% AA, 45 min.)\r\n1 oz. Kent Goldings (5% AA, 30 min.)\r\n1 oz. Kent Goldings (5% AA, 15 min.)');
